styles:
.vclTourHintWrapper {
    position: absolute !important;
    background-color: #fff;
    z-index: 999;
    text-align: center;
    font-size: 14px;
    color: #000;
    border-radius: 5px;
    -webkit-box-shadow: inset 0 0 30px 0 rgba(0, 0, 0, .5);
    box-shadow: inset 0 0 30px 0 rgba(0, 0, 0, .5);
  }
  
.vclTourHintWrapper .header {
  padding: 10px;
  border-bottom: 1px solid #ccc;
  background-color: #ddd;
  border-radius: 5px;
  -webkit-box-shadow: inset 0 0 30px 0 rgba(0, 0, 0, .5);
  box-shadow: inset 0 0 30px 0 rgba(0, 0, 0, .5);
}
  
.vclTourHintWrapper .content {
  padding: 10px;
}

.vclTourHintWrapper .footer {
  padding: 10px;
}

.vclTourHintWrapper .vclButton:first {
  margin-left: 0;
}
  
.vclTourHintWrapper.transformX_50.transformY_100 {
  -webkit-transform: translateX(-50%) translateY(-100%);
  transform: translateX(-50%) translateY(-100%);
}
  
.vclTourHintWrapper.transformX_50 {
  -webkit-transform: translateX(-50%);
  transform: translateX(-50%);
}
  
.vclTourHintWrapper.transformY_50 {
  -webkit-transform: translateY(-50%);
  transform: translateY(-50%);
}
  
.vclTourHintWrapper.transformY_50.transformX_100 {
  -webkit-transform: translateX(-100%) translateY(-50%);
  transform: translateX(-100%) translateY(-50%);
}
